package com.cjbdi

import org.apache.kafka.clients.producer.{KafkaProducer, ProducerRecord}
import org.apache.kafka.common.serialization.StringSerializer

import java.util.Properties

/**
 * 生产者轮询写入多个分区
 */
object ProducerDemo2 {

  def main(args: Array[String]): Unit = {

    // 1 配置参数
    val props = new Properties()
    // 连接kafka节点
    props.setProperty("bootstrap.servers", "rookiex01:9092,rookiex02:9092,rookiex03:9092")
    //指定key序列化方式
    props.setProperty("key.serializer", "org.apache.kafka.common.serialization.StringSerializer")
    //指定value序列化方式
    props.setProperty("value.serializer", classOf[StringSerializer].getName) // 两种写法都行

    val topic = "xx1" //三个分区{0, 1, 2}

    // 2 kafka的生产者
    val producer: KafkaProducer[String, String] = new KafkaProducer[String, String](props)
      // 3 封装的对象

      val mes = "{\"base64File\":\"\",\"wsBean\":{\"database\":\"fb15_10\",\"fbId\":\"15\",\"host\":\"192.1.48.226\",\"schema\":\"db_mses\",\"t_c_baah\":\"(2021)兵01民终74号\",\"t_c_jbfymc\":\"新疆生产建设兵团第一师中级人民法院\",\"t_c_stm\":\"1047304105d1e2b942f0a81ddd1cd12ed52d\",\"t_d_sarq\":\"2021-03-19 00:00:00\",\"t_n_jbfy\":\"4167\",\"ws_c_mc\":\"【上网文书】李丕辉与麦麦提？麦提尼亚孜劳务合同纠纷二审民事判决书.docx\",\"ws_c_nr\":\"/4166/4167/0302/294/416720210302609702/1047aa3601bbb0006cccc416700000609702-1.docx\",\"ws_n_lb\":\"4\"}}"
      val record = new ProducerRecord[String, String](topic, mes)
      producer.send(record) //将将数据发送到Kafka（是一条一条的发送吗？，还是先在客户端缓存，达到一定的大小在批量发送）

    println("message send success")

    //producer.flush()
    // 释放资源
    producer.close()
  }

}
